package com.misys.tiplus2.customisation.entity.persister;

import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;

import com.misys.tiplus2.persistent.EntityPersister;
import com.misys.tiplus2.customisation.entity.FSPF;
import com.misys.tiplus2.customisation.entity.FSPF.Key_FSPF;

@SuppressWarnings("unchecked")
public class FSPFPersister extends EntityPersister {

	// This code will be generated by the builder 
	
	public FSPFPersister() {
		setKeyMembers(new MemberDescriptor[] {
            new MemberDescriptor("fsing", "FSPF.FSING", getBinderFactory().getBinderForType("String")),
		});
			
		setMembers(new MemberDescriptor[] {
            new MemberDescriptor("fsingd", "FSPF.FSINGD", getBinderFactory().getBinderForType("String")),
            new MemberDescriptor("mnt_in_bo", "FSPF.MNT_IN_BO", getBinderFactory().getBinderForType("String")),
		});

		setForeignKeyMappings(new ForeignKeyMappingDescriptor[] {
		});
	}

    private static final String tableName = "FSPF";

    /**
     * Gets the table name - in this case FSPF
     * <p>
     * Note that the <i>schema</i> must be prefixed to this when constructing
     * SQL statements, for example: <code>schema.FSPF</code>
     * 
     * @return the table name
     */
	@Override
	public String getTableName() {       
		return tableName;
	}

	@Override
	public Class<?> getEntityClass() {
		return FSPF.class;
	}

	public Class<?> getKeyClass() {
		return Key_FSPF.class;
	}

	@Override
	public FSPF constructEntity(final Object[] keyValues, final Object[] resultRow) {
		final FSPF entity = new FSPF();
		
		// Key members
		final Key_FSPF key = entity.get$key();
		assignKeyValues(key, keyValues);

		assignMemberValues(entity, resultRow);
		
		return entity;
	}
	
	@Override
	public Key_FSPF constructKey(final Object[] keyValues) {
		final Key_FSPF key = new Key_FSPF();
		assignKeyValues(key, keyValues);
		return key;
	}
	
	@Override
	public void assignKeyValues(final Object object, final Object[] resultRow) {
		final Key_FSPF result = (Key_FSPF)object;
		int index = 0;
		result.setFsing((java.lang.String)resultRow[index++]);
	}
	
	@Override
	public void assignMemberValues(final Object object, final Object[] resultRow) {
		final FSPF result = (FSPF)object;
		int index = 0;
		result.setFsingd((java.lang.String)resultRow[index++]);
		result.setMnt_in_bo((java.lang.String)resultRow[index++]);
	}

	@Override
	public Object[] getKeyMemberValues(final Object object) {
		final Key_FSPF entity = (Key_FSPF)object;
		final Object[] result = new Object[1];
		int index = 0;
		result[index++] = entity.getFsing();
		return result;
	}

	@Override
	public Object[] getMemberValues(final Object object) {
		final FSPF entity = (FSPF)object;
		final Object[] result = new Object[2];
		int index = 0;
		result[index++] = entity.getFsingd();
		result[index++] = entity.getMnt_in_bo();
		return result;
	}

	@Override
	public Object[] getMemberValuesForMember(final String memberName, final Object object) {
		throw new IllegalArgumentException("Not a complex member: " + memberName);
	}

	@Override
	public boolean requiresOptimisticLocking() {
		return false;
	}

	@Override
	public Object[] extractKeyValues(ResultSet resultSet) throws SQLException {
		return super.extractKeyValues(resultSet);
	}

	@Override
	public int bindKeyValues(Object[] keyValues, PreparedStatement stmt, final int startIndex) throws SQLException {
		return super.bindKeyValues(keyValues, stmt, startIndex);
	}

    @Override
	public Object getKeyFromEntity(final Object entity) {
		return (((FSPF)entity).get$key());
	}

}
